import React, { useState } from "react";
import { Game } from "./Game";

const App = () => {
  const [input, setInput] = useState(null);
  const [playGame, setPlayGame] = useState(false);
  const [gameKey, setGameKey] = useState(0);

  const handleButtonSelect = (choice) => {
    setInput(choice);
    setGameKey((prevKey) => prevKey + 1);
    // setLoading(true);
    setTimeout(() => {
      setPlayGame(true);
    }, 1000); // Adjust the timeout as needed
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-900 text-white p-8">
      <div className="max-w-lg w-full text-center">
        <h2 className="text-3xl font-bold mb-6">Seven Up Seven Down Game</h2>
        <div className="mb-6 flex justify-center gap-4">
          <button
            className={`py-2 px-4 rounded font-bold ${
              input === "up" ? "bg-blue-700" : "bg-blue-600 hover:bg-blue-700"
            }`}
            onClick={() => handleButtonSelect("up")}
            // disabled={loading}
          >
            Up
          </button>
          <button
            className={`py-2 px-4 rounded font-bold ${
              input === "down" ? "bg-blue-700" : "bg-blue-600 hover:bg-blue-700"
            }`}
            onClick={() => handleButtonSelect("down")}
            // disabled={loading}
          >
            Down
          </button>
        </div>
        {
          // !loading &&
          playGame && (
            <Game
              key={gameKey}
              input={input}
              // setLoading={setLoading}
            />
          )
        }
      </div>
    </div>
  );
};

export default App;
