import { Contract, networks } from "seven-up-seven-down";
import { isConnected, getPublicKey } from "@stellar/freighter-api";
import React, { useState } from "react";

// Get the user's public key, if Freighter is installed.
let addressLookup = async () => {
  if (await isConnected()) return getPublicKey();
};

let address;

// Returning the same object identity every time avoids unnecessary re-renders
const addressObject = {
  address: "",
  displayName: "",
};

export const Game = () => {
  const [publicKey, setPublicKey] = useState(null);

  React.useEffect(() => {
    const tracker = new Contract({
      contractId: networks.futurenet.contractId,
      networkPassphrase: networks.futurenet.networkPassphrase,
      rpcUrl: "https://rpc-futurenet.stellar.org/",
    });

    tracker
      .play({ prediction: "up" })
      .then((result) => {
        result.getPublicKey().then(setPublicKey);
      })
      .catch((error) => {
        console.error("Error playing game:", error);
      });
  }, []);

  return (
    <>
      <h1>
        <span className="text-gradient">{publicKey}</span>
      </h1>
    </>
  );
};
