import React, { useState } from "react";
import { Game } from "./Game"; // Assuming Game.js is in the same directory

const App = () => {
  const [input, setInput] = useState("up"); // Default value is 'up'

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  return (
    <div>
      <h2>Seven Up Seven Down Game</h2>
      <div>
        <label>
          Choose your prediction:
          <select value={input} onChange={handleInputChange}>
            <option value="up">Up</option>
            <option value="down">Down</option>
          </select>
        </label>
      </div>
      <Game input={input} />
    </div>
  );
};

export default App;
