import { Contract, networks } from "seven-up-seven-down";
import React, { useEffect, useState } from "react";

export const Game = ({ input, loading, setLoading }) => {
  const upOrDown = new Contract({
    contractId: networks.futurenet.contractId,
    networkPassphrase: networks.futurenet.networkPassphrase,
    rpcUrl: "https://rpc-futurenet.stellar.org/",
  });

  const [gambled, setGambled] = useState(null);

  useEffect(() => {
    if (input) {
      setLoading(true);
      upOrDown
        .play({ prediction: input })
        .then((result) => {
          setGambled(result.result.join(" "));
        })
        .catch((error) => {
          console.error("Error playing game:", error);
        })
        .finally(() => {
          setLoading(false);
        });
    }
  }, [input, setLoading]);

  // Determine the text color based on the game result
  const resultColor = gambled?.includes("won") ? "#4daaa9" : "#f9db4a"; // Teal-like for win, Gold-like for loss

  return (
    <div className="mt-6 text-center">
      {loading && (
        <div style={{ color: "#f9db4a" }} className="mt-4 text-center">
          Loading...
        </div>
      )}
      <h1 className="text-2xl font-bold">
        <span style={{ color: resultColor }}>{gambled}</span>
      </h1>
    </div>
  );
};
