import React, { useState } from "react";
import { Game } from "./Game";
import DiceAnimation from "./DiceAnimation"; // Import the DiceAnimation component

const App = () => {
  const [input, setInput] = useState("up");
  const [playGame, setPlayGame] = useState(false);
  const [gameKey, setGameKey] = useState(0);
  const [loading, setLoading] = useState(false);

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  const handleSubmit = () => {
    setGameKey((prevKey) => prevKey + 1); // Increment key to force re-render of Game
    setPlayGame(false); // Reset playGame to ensure Game component correctly responds to loading
    setLoading(true); // Start loading animation
    setTimeout(() => {
      setPlayGame(true); // Start game after a brief delay
    }, 100); // Delay can be adjusted based on your needs
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-8">
      <div className="max-w-lg mx-auto">
        {loading && <DiceAnimation />} {/* Show dice animation when loading */}
        <h2 className="text-3xl font-bold mb-4 text-center">
          Seven Up Seven Down Game
        </h2>
        <div className="mb-4">
          <label className="block mb-2">
            Choose your prediction:
            <select
              className="w-full p-2 rounded bg-gray-800 border border-gray-700"
              value={input}
              onChange={handleInputChange}
            >
              <option value="up">Up</option>
              <option value="down">Down</option>
            </select>
          </label>
        </div>
        <button
          className="w-full py-2 px-4 bg-blue-600 hover:bg-blue-700 rounded text-white font-bold"
          onClick={handleSubmit}
        >
          Submit
        </button>
        {/* Render Game component only after submission and loading completion */}
        {!loading && playGame && (
          <Game key={gameKey} input={input} setLoading={setLoading} />
        )}
      </div>
    </div>
  );
};

export default App;
