import { Contract, networks } from "seven-up-seven-down";
import React, { useEffect, useState } from "react";
import { greenColor, redColor, secondaryColor, textColor } from "./colors";

export const Game = ({ input, loading, setLoading }) => {
  const upOrDown = new Contract({
    contractId: networks.futurenet.contractId,
    networkPassphrase: networks.futurenet.networkPassphrase,
    rpcUrl: "https://rpc-futurenet.stellar.org/",
  });

  const [gambled, setGambled] = useState(null);

  useEffect(() => {
    if (input) {
      setLoading(true);
      upOrDown
        .play({ prediction: input })
        .then((result) => {
          setGambled(result.result.join(" "));
        })
        .catch((error) => {
          console.error("Error playing game:", error);
        })
        .finally(() => {
          setLoading(false);
        });
    }
  }, [input, setLoading]);

  // Function to render the game result with different styles
  const renderGameResult = (result) => {
    if (!result) return null;

    const parts = result.split(" ");
    if (parts.length > 1) {
      const resultWord = parts[1];
      const number = parts[7];

      return (
        <>
          <span style={{ color: textColor }}>{parts[0]}</span>{" "}
          <span
            style={{ color: resultWord === "lost" ? redColor : greenColor }}
          >
            {resultWord}
          </span>{" "}
          <span style={{ color: textColor }}>{parts[4]}</span>{" "}
          <span style={{ color: textColor }}>{parts[5]}</span>{" "}
          <span style={{ color: textColor }}>{parts[6]}</span>{" "}
          <span
            style={{
              color: resultWord === "won" ? secondaryColor : primaryColor,
            }}
          >
            {number}
          </span>
        </>
      );
    }

    return result;
  };

  return (
    <div className="mt-6 text-center">
      {loading && (
        <div className="text-center" style={{ color: "#f9db4a" }}>
          Loading...
        </div>
      )}
      <h1 className="text-2xl font-bold">{renderGameResult(gambled)}</h1>
    </div>
  );
};
