import React, { useState } from "react";
import { Game } from "./Game"; // Assuming Game.js is in the same directory

const App = () => {
  const [input, setInput] = useState("up");
  const [playGame, setPlayGame] = useState(false);
  const [key, setKey] = useState(0); // To force re-render of Game
  const [loading, setLoading] = useState(false);
  const [gameKey, setGameKey] = useState(0); // Using a key to force re-render

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  const handleSubmit = () => {
    setPlayGame(true);
    setKey((prevKey) => prevKey + 1); // Increment key to re-render Game
    setGameKey((prevKey) => prevKey + 1); // Increment key to force re-render

    setLoading(true); // Start loading when submit is clicked
  };

  return (
    <div>
      <h2>Seven Up Seven Down Game</h2>
      <div>
        <label>
          Choose your prediction:
          <select value={input} onChange={handleInputChange}>
            <option value="up">Up</option>
            <option value="down">Down</option>
          </select>
        </label>
      </div>
      <button onClick={handleSubmit}>Submit</button>
      {loading && <div>Loading...</div>}
      {playGame && <Game key={gameKey} input={input} setLoading={setLoading} />}
    </div>
  );
};
export default App;
